serialization.onAfterDeserialize:
objJSX.openProject = function() {
  var objSelect = this.getDescendantOfName('projectSelect');
  var objRecord = objSelect.getRecord(objSelect.getValue());
  if (objRecord && objRecord.jsxid) {
    jsx3.ide.doOpenProject(objRecord.jsxid);
  }
};

objJSX.populateProjectMenu = function(objMenu, strRecordId) {
  jsx3.ide.populateProjectMenu(objMenu, strRecordId || "userproj");
};

objJSX.onCheck = function(objCB) {
  var settings = jsx3.ide.getIDESettings();
  settings.set('prefs', 'builder', 'welcome_v', jsx3.getVersion(), objCB.getChecked() == jsx3.gui.CheckBox.CHECKED);
};

objJSX.onOpenExisting = function(e) {
  var objEvent = jsx3.gui.Event.wrap(e);
  this.getDescendantOfName("projectSelect").showContextMenu(objEvent);
};

objJSX.openHelpContents = function() {
  var file = jsx3.ide.getSystemRelativeFile('doc/html/index.htm');
  if (file.isFile())
    jsx3.ide.openConsoleWindow('doc/html/index.htm', 'jsxide_doc',null,null,'yes','yes','yes','yes','yes');
  else {
    var url = jsx3.IDE.getDynamicProperty("builder_url");
    jsx3.IDE.alert("Documentation Not Installed",
        "It appears that the online documentation is not installed correctly. " +
        "Please <a href='"+ url +"' target='_blank'>download the General Interface&#8482; documentation</a> and make sure that it is installed in the doc/ directory.");
  }
};

objJSX.openAPIDoc = function() {
  var file = jsx3.ide.getSystemRelativeFile('api/xml/index.xml');
  if (file.isFile())
    jsx3.ide.doOpenApiHelp();
  else
    jsx3.IDE.alert("API Documentation Not Installed",
        "It appears that the API documentation is not installed correctly in the api/xml directory.");
};

objJSX.onNewProject = function() {
  jsx3.ide.newProject();
};

objJSX.onJoin = function() {
  var url = jsx3.IDE.getDynamicProperty("signup_url");
  jsx3.ide.openConsoleWindow(url,
      'jsxide_devsignup',null,null,'yes','yes','yes','yes','yes');
};

objJSX.onInit = function() {
  var settings = jsx3.ide.getIDESettings();

  // set checked state of "show dialog" check box
  var bOpen = settings.get('prefs', 'builder', 'welcome_v', jsx3.getVersion());
  this.getDescendantOfName('chk_open').setChecked(bOpen || bOpen == null);

  // give context-sensitive help hint
  var helpKey = jsx3.IDE.getDynamicProperty("jsx3.app.Server.help." + jsx3.app.Browser.getSystem()) ||
                jsx3.IDE.getDynamicProperty("jsx3.app.Server.help");
  if (helpKey) {
    var key = jsx3.gui.HotKey.valueOf(helpKey);
    this.getDescendantOfName('ctxHelpTip').setText("Type <b>" + key.getFormatted() + "</b> anywhere in General Interface Builder for context-sensitive help.", true);
  }

  // set close button icon (resolve URI)
  var closeBtn = this.getDescendantOfName('btn_close');
  closeBtn.setText('<img src="' + this.getServer().resolveURI("components/welcome/close-icon.gif") +
      '" width="12" height="12" style="vertical-align:top;margin-right:4px;"/>' + closeBtn.getText(), true);

  // set version string
  var v = jsx3.getVersion().split(/\./g);
  v = v.slice(0, v[2] == 0 ? 2 : 3).join(".");
  this.getDescendantOfName('version').setText("Version " + v);

  // set recent projects
  var recentHTML = "";
  var doOpenProject = "doOpenProject";
  var iconURI = this.getServer().resolveURI("images/icon_82.gif");

  var projects = jsx3.ide.getRecentProjects();
  for (var i = 0; i < 6 && i < projects.length; i++) {
    var path = jsx3.ide.fixPath(projects[i]);
    var label = path;
    if (label.indexOf(jsx3.APP_DIR_NAME) == 0)
      label = label.substring(jsx3.APP_DIR_NAME.length + 1);
    label = jsx3.util.strTruncate(label, 24, null, 0);

    recentHTML += '<img src="' + iconURI + '"/>' + 
        '<a href="#" target="_blank" onclick="jsx3.ide.' + doOpenProject +
        '(\'' + path + '\'); return false;">' + label + '</a><br/>';
  }
  this.getDescendantOfName('recentProjects').setText(recentHTML);

  this.getDescendantOfName("updatesRSS").subscribe("xmlbind", this, "_onUpdatesBind");
  this.getDescendantOfName("forumRSS").subscribe("xmlbind", this, "_onForumBind");
};

objJSX._onUpdatesBind = function(objEvent) {
  if (objEvent.xml.getNamespaceURI() == jsx3.app.Cache.XSDNS &&
     (objEvent.xml.getNodeName() == "error" || objEvent.xml.getNodeName() == "timeout")) {
    objEvent.target.unsubscribe("xmlbind", this);
    objEvent.target.resetXmlCacheData();
    objEvent.target.setXMLURL(null);
    objEvent.target.setXMLString(
      '<rss><channel>' +
      '<item>' +
      '  <title>Got the latest?</title>' +
      '  <link>http://developer.tibco.com/gi</link>' +
      '  <description>Visit the General Interface Developer Center for the latest updates, news, articles, tutorials, how-to videos, discussions, downloads, development utilities, and more.</description>' +
      '</item>' +
      '<item>' +
      '  <title>Where\'s the source code?</title>' +
      '  <link>http://gi.tibco.com</link>' +
      '  <description>The General Interface open source project home page connects you with the source code for GI, bug tracking, enhancement requests, specialized GI builds, and more useful tools.</description>' +
      '</item>' +
      '</channel></rss>'
    );
  }
};

objJSX._onForumBind = function(objEvent) {
  if (objEvent.xml.getNamespaceURI() == jsx3.app.Cache.XSDNS &&
     (objEvent.xml.getNodeName() == "error" || objEvent.xml.getNodeName() == "timeout")) {
    var dev_url = jsx3.IDE.getDynamicProperty("dev_url");
    var sample_url = jsx3.IDE.getDynamicProperty("sample_url").replace("&", "&amp;");
    var feedback_url = jsx3.IDE.getDynamicProperty("feedback_url");

    objEvent.target.unsubscribe("xmlbind", this);
    objEvent.target.resetXmlCacheData();
    objEvent.target.setXMLURL(null);
    objEvent.target.setXMLString(
      '<rss><channel>' +
      '<item>' +
      '  <title>GI Development</title>' +
      '  <link>'+ dev_url +'</link>' +
      '</item>' +
      '<item>' +
      '  <title>GI Sample Applications</title>' +
      '  <link>'+ sample_url +'</link>' +
      '</item>' +
      '<item>' +
      '  <title>GI Feedback</title>' +
      '  <link>'+ feedback_url +'</link>' +
      '</item>' +
      '</channel></rss>'
    );
  }
};

objJSX.onInit();
