strings.jsxvalue:
/*create http request object */
  var objHTTP = new jsx3.net.Request();
   /*open connection; pass true for async */
 objHTTP.open('GET',location.href,true);
 /* subscribe to 'response' event */
  objHTTP.subscribe(jsx3.net.Request.EVENT_ON_RESPONSE,function(objEvent) {
    /* get handle to the http object (this is accessible via the event's 'target' field */
    var objHTTP = objEvent.target;
    var alertWindow = {
     width:400,
     height:400,
    scrollbar:true
    };
    var msg = objHTTP.getResponseText();
     /*alert user to the underlying source for the page    //publish */
 console.log(msg);   eg.pubsub.logger.info('Example2 Subscriber: Received asynchronous response from web link' ,null);
    eg.pubsub.getServer().alert('Response notified', jsx3.util.strEscapeHTML(msg), null, 'OK', alertWindow);
 });
 /* send the request */
  objHTTP.send();
