serialization.onAfterDeserialize:
this.onWidthChange = function(strValue) {
  block.setWidth(strValue, true);
};

this.onHeightChange = function(strValue) {
  block.setHeight(strValue, true);
};

this.onLeftChange = function(strValue) {
  block.setLeft(strValue, true);
};

this.onTopChange = function(strValue) {
  block.setTop(strValue, true);
};

desc.setText(root.getMetaValue("description"));
name.setText(root.getMetaValue("name"));
width.setValue(block.getWidth());
height.setValue(block.getHeight());
left.setValue(block.getLeft());
top.setValue(block.getTop());
