serialization.onAfterDeserialize:
this.onFormatChange = function(strValue) {
  var value = strValue;
  if (strValue == "1" || strValue == "2" || strValue == "3" || strValue == "4") {
    var locale = this.getServer().getLocale();
    value = jsx3.util.DateFormat.getDateInstance(parseInt(strValue), locale).getFormat();
  }
  datepicker.setFormat(value);
  datepicker.repaint();
};

this.onLocaleSelect = function(strRecordId) {
  var server = this.getServer();
  server.setLocale(jsx3.util.Locale.valueOf(strRecordId));
  server.reloadLocalizedResources();
  this.onFormatChange(format.getValue());
};

format.setValue(datepicker.getFormat());
locale.setValue(this.getServer().getLocale().getLanguage());
